package codewithharry;

public class precedence {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		float a = 7/4*9/2;
		System.out.println(a);
		//encrypt
		char grade = 'B';
		grade = (char)(grade + 8);
		System.out.println(grade);
		//decrypt
		char Grade = 'B';
		Grade = (char)(grade - 8);
		System.out.println(Grade);

	}

}
